import { Link } from 'react-router-dom';
import './css/info.css';
import { useState } from 'react';

function Info() {
    console.log('Info');
    const [info, setInfo] = useState([]);

    


    useEffect(() => {
        fetch('https://witzapi.de/api/joke')
        .then(response => response.json())
        .then(data => {
            console.log(data);
            const joke = data[0].text;
            const jokeDiv = document.getElementById('jokeDiv');
            jokeDiv.innerHTML = joke;
            setInfo(prevInfo => [...prevInfo, joke]); // Update setInfo to append the new joke to the info array
        }) 
    }, []);

    return (
        <div id='infoContainer'>
            <button 
            onClick={()=> {

            }}
            className='btn btn-primary'
            >
                Click me
            </button>
            <div id='jokeDiv'></div>
            <Link to='/home' className='btn btn-primary'>Home</Link>
        </div>
    );
}

export default Info;
