import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./css/info.css";

function Info() {
    console.log("Info");
    const [info, setInfo] = useState("");
    const [loadJoke, setLoadJoke] = useState(false);
    const [buttonClicked, setButtonClicked] = useState(true);

    useEffect(() => {
        if (buttonClicked) {
            fetch("https://witzapi.de/api/joke")
                .then((response) => response.json())
                .then((data) => {
                    console.log(info);
                    const joke = data[0].text;
                    if(!info) setInfo(joke);
                    setInfo([...info, joke]);
                    setLoadJoke(!loadJoke);
                });
            setButtonClicked(false);
        }
    }, [buttonClicked]);

    return (
        <div id="infoContainer">
            <button onClick={() => setButtonClicked(true)} className="btn btn-primary">
                Click me
            </button>
            <div id="jokeDiv">{info}</div>
            <Link to="/home" className="btn btn-primary">
                Home
            </Link>
        </div>
    );
}

export default Info;
