import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import './css/info.css';
import { useState } from 'react';

function Info() {
    console.log('Info');
    const [info, setInfo] = useState(false);
    

    useEffect(() => {
        fetch('https://witzapi.de/api/joke')
            .then(response => response.json())
            .then(data => {
                setInfo(data);
            });
    }, []);

    useEffect(() => {
        console.log(info);
    }, [info]);

    return (
        <div id='infoContainer'>
            <button onClick={() => {
                fetch('https://witzapi.de/api/joke')
                    .then(response => response.json())
                    .then(data => {
                        setInfo(...info ,data);
                        const jokeDiv = document.createElement('div');
                        jokeDiv.textContent = data.joke;
                        document.getElementById('infoContainer').appendChild(jokeDiv);
                    });
            }}>
                Click me
            </button>
        </div>
    );
}

export default Info;
