import React, { useContext } from 'react';
import './css/game.css';
import ImageDisplay from './components/imageViewer';
import ImageContext from './contexts/imageContext';
import MapComponent from './components/map/mapComponent';
import MapCoordinatesContext from './contexts/mapContext';
import haversine from './components/haversine';
import OpenStreetCam from './components/openstreetcam';
import SubmitGuessButton from './components/submitGuessButton';
import GameContext from './contexts/gameContext';
import { redirect } from 'react-router-dom';




function Game() {
    console.log('Game');
    const { coordinates } = React.useContext(MapCoordinatesContext);
    const { imageInfo } = useContext(ImageContext);
    const { gameInfo, setGameInfo } = useContext(GameContext);
    
    if(gameInfo === null) OpenStreetCam()
    const newGameInfo = {...gameInfo};

    let dist = null;
    if(coordinates && imageInfo) {
        dist = haversine(coordinates.lat, coordinates.lng, imageInfo[1], imageInfo[2]);
        newGameInfo.distance = dist;
        newGameInfo.lat = coordinates.lat;
        newGameInfo.lng = coordinates.lng;
        newGameInfo.latImg = imageInfo[1];
        newGameInfo.lngImg = imageInfo[2];
        
    }
    if(newGameInfo.eingeloggt === true && newGameInfo.distance !== null) {
        setGameInfo(newGameInfo);
        redirect('/home');
    }
    

    
    return (
        <div className='gameContainer'>
            <h1>KölnGuesser {coordinates && imageInfo  ? `Lat: ${coordinates.lat}, Lng: ${coordinates.lng}, LatImg: ${imageInfo[1]}, LngImg ${imageInfo[2]}` : ''}</h1>
            <MapComponent />
            <ImageDisplay />
            <SubmitGuessButton />
            <OpenStreetCam />
            <h2>{dist ? `Distance: ${dist} km` : ''}</h2>
        </div>
    );
}



export default Game;
