import { useContext, useEffect } from 'react';
import ImageContext from "./imageContext";

function OpenStreetCam() {
    const { imageInfo,  setImageInfo } = useContext(ImageContext);

    useEffect(() => {
        // Define the latitude and longitude boundaries of the rectangle
        const latMin = 50.9300;
        const latMax = 50.9400;
        const lngMin = 6.9500;
        const lngMax = 6.9700;

        // Generate a random latitude and longitude within the rectangle
        const lat = Math.random() * (latMax - latMin) + latMin;
        const lng = Math.random() * (lngMax - lngMin) + lngMin;

        console.log("Lat and Lng:", lat, lng);

        fetch(
            `https://api.openstreetcam.org/2.0/photo/?lat=${lat}&lng=${lng}&radius=500`
        )
            .then((response) => {
                console.log("Status:", response.status);
                return response.json();
            })
            .then((data) => {
                const firstPhoto = data.result.data[0];
                console.log("First photo:", firstPhoto.fileurlProc);
                setImageInfo([firstPhoto.fileurlProc, firstPhoto.lat, firstPhoto.lng]);
                console.log(imageInfo)
            })
            .catch((error) => {
                console.error("Error:", error);
            });
    }, []); // Empty array ensures this runs only once after initial render

    return null; // or return your JSX here
}

export default OpenStreetCam;
