import React, { useContext, useEffect } from 'react';
import './css/game.css';
import ImageDisplay from './components/imageViewer';
import ImageContext from './contexts/imageContext';
import MapComponent from './components/map/mapComponent';
import MapCoordinatesContext from './contexts/mapContext';
import haversine from './components/haversine';
import OpenStreetCam from './components/openstreetcam';
import SubmitGuessButton from './components/submitGuessButton';
import GameContext from './contexts/gameContext';
import ScoreContext from './contexts/scoreContext';


function Game() {
    console.log('Game');
    const { coordinates } = React.useContext(MapCoordinatesContext);
    const { imageInfo } = useContext(ImageContext);
    const { gameInfo, setGameInfo } = useContext(GameContext);
    const { scoreInfo, setScoreInfo } = useContext(ScoreContext);

    useEffect(() => {
        if(coordinates && imageInfo) {
            const newGameInfo = {...gameInfo};
            const dist = haversine(coordinates.lat, coordinates.lng, imageInfo[1], imageInfo[2]);
            newGameInfo.distance = dist;
            newGameInfo.lat = coordinates.lat;
            newGameInfo.lng = coordinates.lng;
            newGameInfo.latImg = imageInfo[1];
            newGameInfo.lngImg = imageInfo[2];
            setGameInfo(newGameInfo);
            newGameInfo.score = Math.round(1000 - dist * 100);
        }
    }, [coordinates]); // Run effect when coordinates change


    useEffect(() => {
        if(!scoreInfo) setScoreInfo({Index: 0, distance: 0, score: 0});
        if(gameInfo &&scoreInfo && gameInfo.eingeloggt ) {
            const newScoreInfo = {...scoreInfo};
            newScoreInfo.counter += 1;
            newScoreInfo.distance += gameInfo.distance;
            newScoreInfo.score += gameInfo.score;
            setScoreInfo(...scoreInfo, newScoreInfo);
            console.log(scoreInfo);
        }
    }, [gameInfo]); // Run effect when gameInfo changes
    

    return (
        <div className='gameContainer'>
            <h1>KölnGuesser {gameInfo && gameInfo.eingeloggt  ? `Lat: ${coordinates.lat}, Lng: ${coordinates.lng}, LatImg: ${imageInfo[1]}, LngImg ${imageInfo[2]}` : ''}</h1>
            <MapComponent />
            <ImageDisplay />
            {coordinates ? <SubmitGuessButton /> : ''}
            <OpenStreetCam />
            <h2>{gameInfo && gameInfo.eingeloggt ? `Distance: ${gameInfo.distance} km` : ''}</h2>
        </div>
    );
}



export default Game;
