import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents, useMap } from 'react-leaflet';
import MapCoordinatesContext from './mapctx';

function InvalidateSizeComponent() {
    const map = useMap();

    useEffect(() => {
        function handleTransitionEnd() {
            map.invalidateSize();
        }

        const container = map.getContainer();
        container.addEventListener('transitionend', handleTransitionEnd);

        return () => {
            container.removeEventListener('transitionend', handleTransitionEnd);
        };
    }, [map]);

    return null;
}

function MapComponent() {
    const position = [50.9413, 6.9573]; // Köln Koordinaten
    const [markerPosition, setMarkerPosition] = useState(position);

    function LocationMarker() {
        useMapEvents({
            click(e) {
                setMarkerPosition(e.latlng);
            },
        });

        return markerPosition === null ? null : (
            <Marker position={markerPosition}>
            </Marker>
        );
    }

    return (
        <MapCoordinatesContext.Provider value={markerPosition}>
            <MapContainer center={position} zoom={13}>
                <TileLayer
                    url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
                    attribution='&copy; <a href="http://osm.org/copyright">OpenStreetMap</a> contributors'
                />
                <LocationMarker />
                <InvalidateSizeComponent />
            </MapContainer>
        </MapCoordinatesContext.Provider>
    );
}

export default MapComponent;
