// Importiere die createContext-Funktion aus dem React-Modul
import { createContext } from "react";

// Erstelle ein neues Context-Objekt und weise es der Variable "ExampleContext" zu
const ScoreContext = createContext(null);

// Exportiere das erstellte Context-Objekt als Standardexport der Datei
export default ScoreContext;
