import React, { Component, useEffect } from 'react';
import { useContext } from 'react';

import ScoreContext from '../contexts/scoreContext';
import GameContext from '../contexts/gameContext';

function ScoreDisplay() {
    const { setScoreInfo, scoreInfo } = useContext(ScoreContext);
    const { gameInfo } = useContext(GameContext);

    useEffect(() => {
        if(!scoreInfo){
            if(gameInfo && gameInfo.eingeloggt) {
                setScoreInfo([{
                    Index: 0,
                    distance: gameInfo.distance,
                    score: gameInfo.score,
                    name: gameInfo.name
                }]);
            }
        }
        else {
            if(!gameInfo || !gameInfo.eingeloggt) return;
            const newScoreInfo = {
                Index: scoreInfo.length,
                distance: gameInfo.distance,
                score: gameInfo.score,
                name: gameInfo.name
            };
            console.log(gameInfo.name)
            setScoreInfo([...scoreInfo, newScoreInfo]);
            console.log(scoreInfo);
        }
    }, [gameInfo]); // Run effect when gameInfo changes

    return (
        <div id='scoreContainer' style={{
            position: gameInfo && gameInfo.eingeloggt ? 'fixed' : 'static',
            top: '50%',
            left: '50%',
            transform: gameInfo && gameInfo.eingeloggt ? 'translate(-50%, -50%)' : 'none',
            zIndex: 1,
            boxShadow: gameInfo && gameInfo.eingeloggt ? '0 0 10px rgba(0, 0, 0, 0.5)' : 'none',
            backgroundColor: 'white',
            padding: '20px',
        }}>
            {gameInfo && gameInfo.eingeloggt && scoreInfo ? (
                [...scoreInfo].sort((a, b) => b.score - a.score).map((score, i, arr) => {
                    const maxIndex = Math.max(...arr.map(score => score.Index));
                    return (
                        <p key={i} id='score' style={{ color: score.Index === maxIndex ? 'red' : 'white' }}>{score.name}: {score.score}</p>
                    );
                })
            ) : (
                <p>Score Pending...</p>
            )}
        </div>
    );
}

export default ScoreDisplay;
