import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './game.css';
import ImageDisplay from './imageViewer';
import ImageContext from './imageContext';
import MapComponent from './mapComponent';
import MapCoordinatesContext from './mapctx';
import haversine from './haversine';



function Game() {

    const { coordinates, setCoordinates } = React.useContext(MapCoordinatesContext);
    
    const [imageInfo, setImageInfo] = React.useContext(ImageContext);

    const distance = haversine(coordinates.lat, coordinates.lng, imageInfo[1], imageInfo[2]);
    console.log('distance: ', distance);


    return (
        <div className='gameContainer'>
            <h1>KölnGuesser {coordinates ? `Lat: ${coordinates.lat}, Lng: ${coordinates.lng}` : ''}</h1>
            <MapComponent />
            <ImageDisplay />
            <Link to="/home" className="buttonhome">Go to Page 1</Link>
        </div>
    );
}

export default Game;
