import { useContext, useEffect } from 'react';
import ImageContext from "../contexts/imageContext";

function OpenStreetCam() {
    const { imageInfo,  setImageInfo } = useContext(ImageContext);

    useEffect(() => {
        if (imageInfo) return; // If imageInfo is already set, don't run this code
        // Define the latitude and longitude boundaries of the rectangle
        const latMin = 50.9100;
        const latMax = 50.9600;
        const lngMin = 6.89516;
        const lngMax = 7.06372;

        // Generate a random latitude and longitude within the rectangle
        const lat = Math.random() * (latMax - latMin) + latMin;
        const lng = Math.random() * (lngMax - lngMin) + lngMin;
        console.log("Fetching OpenStreetCam image..."); 
        fetch(
            `https://api.openstreetcam.org/2.0/photo/?lat=${lat}&lng=${lng}&radius=500`
        )
            .then((response) => {
                if(!response.ok) throw new Error("No image found");
                return response.json();
            })
            .then((data) => {
                let numberOfImages = data.result.data.length;
                console.log(`Found ${numberOfImages} images.`);
                if (numberOfImages > 0) {
                    const randomIndex = Math.floor(Math.random() * (numberOfImages -1));
                    const randomPhoto = data.result.data[randomIndex];
                    setImageInfo([randomPhoto.fileurlProc, randomPhoto.lat, randomPhoto.lng]);
                } 
            })
            .catch((error) => {
                console.log(error);
            });
    }, [imageInfo]); // Only run this code when imageInfo changes

    return null; // or return your JSX here
}

export default OpenStreetCam;
