import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import "./css/info.css";

function Info() {
    console.log("Info");
    const [info, setInfo] = useState([]);
    const [loadJoke, setLoadJoke] = useState(false);

    useEffect(() => {
        if (loadJoke) {
            fetch("https://witzapi.de/api/joke")
                .then((response) => response.json())
                .then((data) => {
                    console.log(data);
                    const joke = data[0].text;
                    const jokeDiv = document.getElementById("jokeDiv");
                    jokeDiv.innerHTML = joke;
                    setInfo(joke);
                });
        }
    }, [loadJoke]);

    return (
        <div id="infoContainer">
            <button onClick={() => setLoadJoke(!loadJoke)} className="btn btn-primary">
                Click me
            </button>
            <div id="jokeDiv"></div>
            <Link to="/home" className="btn btn-primary">
                Home
            </Link>
        </div>
    );
}

export default Info;
