import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './game.css';
import ImageDisplay from './imageViewer';
import ImageContext from './imageContext';
import MapComponent from './mapComponent';
import MapCoordinatesContext from './mapctx';
import haversine from './haversine';



function Game() {

    const { coordinates, setCoordinates } = React.useContext(MapCoordinatesContext);
    
    const { imageInfo,  setImageInfo } = useContext(ImageContext);

    let dist = null;
    if(coordinates && imageInfo) {
        dist = haversine(coordinates.lat, coordinates.lng, imageInfo[1], imageInfo[2]);
    }

    // const distance = haversine(coordinates.lat, coordinates.lng, imageInfo[1], imageInfo[2]);

    return (
        <div className='gameContainer'>
            <h1>KölnGuesser {coordinates && imageInfo ? `Lat: ${coordinates.lat}, Lng: ${coordinates.lng}, LatImg: ${imageInfo[1]}, LngImg ${imageInfo[2]}` : ''}</h1>
            <MapComponent />
            <ImageDisplay />
            <Link to="/home" className="buttonhome"> {dist ? 'Distance: ${dist}': 'go Back'} </Link>
        </div>
    );
}

export default Game;
