import React, { useEffect, useRef } from 'react';
import { Viewer } from 'mapillary-js';

function MapillaryViewer({ accessToken, imageId }) {
  const viewerContainerRef = useRef(null);

  useEffect(() => {
    if (accessToken && imageId && viewerContainerRef.current) {
      new Viewer({
        accessToken: accessToken,
        container: viewerContainerRef.current,
        imageId: imageId
      });
    }
  }, [accessToken, imageId]);

  return (
    <div ref={viewerContainerRef} style={{ width: 400, height: 300 }}></div>
  );
}

export default MapillaryViewer;
