import React, { Component, useEffect } from 'react';
import { useContext } from 'react';

import ScoreContext from '../contexts/scoreContext';
import GameContext from '../contexts/gameContext';

function ScoreDisplay() {
    const { setScoreInfo, scoreInfo } = useContext(ScoreContext);
    const { gameInfo } = useContext(GameContext);

    useEffect(() => {
        if(!scoreInfo){
            if(gameInfo && gameInfo.eingeloggt) {
                setScoreInfo([{
                    Index: 0,
                    distance: gameInfo.distance,
                    score: gameInfo.score,
                    name: gameInfo.name
                }]);
            }
        }
        else {
            if(!gameInfo || !gameInfo.eingeloggt) return;
            const newScoreInfo = {
                Index: scoreInfo.length,
                distance: gameInfo.distance,
                score: gameInfo.score,
                name: gameInfo.name
            };
            console.log(gameInfo.name)
            setScoreInfo([...scoreInfo, newScoreInfo]);
            console.log(scoreInfo);
        }
    }, [gameInfo]); // Run effect when gameInfo changes

    return (
        <div id='scoreContainer'>
            {gameInfo && gameInfo.eingeloggt && scoreInfo ? (
                [...scoreInfo].sort((a, b) => b.score - a.score).map((score, i) => (
                    <p key={i} id='score' style={{ color: i === 0 ? 'red' : 'white' }}>Name: {score.name} Score: {score.score}</p>
                ))
            ) : (
                <p>Score Pending...</p>
            )}
        </div>
    );
}

export default ScoreDisplay;
