import React, { useEffect, useState } from 'react';

function StreetView() {
    const [imageUrl, setImageUrl] = useState('');

    useEffect(() => {
        fetch('https://a.mapillary.com/v3/images?client_id=1320832639310133&closeto=10.75,59.95&radius=1000&per_page=1')
            .then(response => response.json())
            .then(data => {
                const imageUrl = `https://images.mapillary.com/${data.features[0].properties.key}/thumb-2048.jpg`;
                setImageUrl(imageUrl);
            });
    }, []);

    return (
        <div>
            {imageUrl && <img src={imageUrl} alt="Street View" />}
        </div>
    );
}

export default StreetView;
